import { Component, Output, EventEmitter } from '@angular/core';

type Products = {
  name: string;
  description: string;
}

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css'],
})
export class ProductsComponent {
  // products!: Products[] | [];
  displayAddProductModal: boolean = false;

  products = [
    { name: "Fish hook", description: "For baiting and catching fish"},
    { name: "Anonymitter", description: "The Anonymous identifier" }
  ]



  // show 'add product' modal
  addNewProduct(e: any){ 
    e.preventDefault()
    this.displayAddProductModal = true 
  }
  // close 'add product' modal from child component 
  onAddProductModal(e: any){ 
    e.preventdefault()
    this.displayAddProductModal = false 
  }

}
