import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { AppServiceService } from 'src/app/service/app-service.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-user-registration',
  templateUrl: './user-registration.component.html',
  styleUrls: ['./user-registration.component.css']
})
export class UserRegistrationComponent {

  constructor( 
    private fb: FormBuilder,
    private api: AppServiceService,
    private router: Router
  ){}

  loading: boolean = false;

  registerForm = this.fb.group({
    name: ['', [Validators.required]],
    email: ['', [Validators.required]],
    password: ['', [Validators.required]],
  });

  onSubmit(){
    this.loading = true;
    if(this.registerForm.value.name && this.registerForm.value.email && this.registerForm.value.password){
      this.api.userRegistration(this.registerForm.value.name, this.registerForm.value.email, this.registerForm.value.password)
      .subscribe({
        next: (res) => {
          this.loading = false;
          localStorage.setItem('user_data', JSON.stringify(res));
          this.router.navigate(['/all-products']);
          console.log(res);
        },
        error: () => console.log('could not sign up')
      })
    }
  }

  get name(){
    return this.registerForm.get('name');
  }

  get email(){
    return this.registerForm.get('email');
  }

  get password(){
    return this.registerForm.get('password');
  }
}
<div class="overlay">
    <div class="custom-modal modal-height" tabindex="-1">
        <div class="modal-dialog">
            <div class="modal-content">
                <div class="modal-header">
                    <h1 class="text-center">Sign Up</h1>
                </div>
                <div class="modal-body">
                   <form 
                    [formGroup]="registerForm" 
                    class="form-body" 
                    (ngSubmit)="onSubmit()">
                        <div>
                            <label for="name">Name</label><br/>
                            <input 
                                type="name" 
                                id="name" 
                                class="form-control"
                                formControlName="name"/>
                            <p *ngIf="name?.errors?.['required']" 
                                class="text-danger">Name field cannot be empty</p>
                        </div>
                        <div>
                            <label for="email">Email</label><br/>
                            <input 
                                type="email" 
                                id="email" 
                                class="form-control"
                                formControlName="email"/>
                            <p *ngIf="email?.errors?.['required']" 
                                class="text-danger">Email field cannot be empty</p>
                        </div>
                        <div>
                            <label for="password">Password</label><br/>
                            <input 
                                type="password" 
                                id="password" 
                                class="form-control"
                                formControlName="password"/>
                            <p *ngIf="password?.errors?.['required']" 
                                class="text-danger">Password field cannot be empty</p>
                        </div>
                        <input 
                            type="submit" 
                            class="btn btn-primary w-100 mt-3" 
                            value="Register" />
                   </form>
                </div>
                <p class="bottom-text">
                    Already have an account? &nbsp;<a href="login">Login</a>
                </p>
            </div>
        </div>
    </div>
</div>
