import { Component, EventEmitter, Output, Input } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { AppServiceService } from 'src/app/service/app-service.service';

@Component({
  selector: 'app-edit-product',
  templateUrl: './edit-product.component.html',
  styleUrls: ['./edit-product.component.css']
})
export class EditProductComponent {

  @Output() closeEditModal = new EventEmitter();
  @Input() id!: string;
  @Input() pr_name!: string;
  @Input() price!: number;
  @Input() pr_description!: string;


  constructor( 
    private fb: FormBuilder,
    private api: AppServiceService,
    private toast: HotToastS
   ){}

  editProductForm = this.fb.group({
    name: ['', [Validators.required]],
    price: [0],
    description: ['', [Validators.required]],
  });

  submit(){
    event?.preventDefault();
    this.api.editProduct(this.id, this.pr_name, this.price, this.pr_description)
    .subscribe({
      next: (res) => {
        this.toast.success('Update Successful')
      }
    })
    // console.log(this.editProductForm.value)
  }

  get name(){
    return this.editProductForm.get('name');
  }
  get description(){
    return this.editProductForm.get('description');
  }

}
