import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class AppServiceService {

  private baseUrl = "https://openfabric-backend.onrender.com"
  constructor(private http: HttpClient) { }


  headers = new HttpHeaders().set('Content-Type', 'application/json');


  getToken() {
    const user_data = localStorage.getItem('user_data');
    if(user_data){
      const data = JSON.parse(user_data);
      this.headers.set('Authorization', 'Bearer ' + data.token);
    }
  }


  // User service 
  userlogin(email: string, password: string){
    return this.http.post(`${this.baseUrl}/api/users/login`, {
      email,
      password
    })
  };

  userRegistration(name: string, email: string, password: string){
    return this.http.post(`${this.baseUrl}/api/users`, {
      name,
      email,
      password
    })
  }

  // products service 
  postProduct(name: string, description: string){
    return this.http.post(`${this.baseUrl}/api/products`, {
      name,
      description
    })
  }

  deleteProduct(id: string){
    return this.http.delete(`${this.baseUrl}/api/products/${id}`)
  }

  editProduct(id: string, name: string, description: string){
    return this.http.put(`${this.baseUrl}/api/products/${id}`, {
      name,
      description
    })
  }

  getSingleProduct(id: string){
    return this.http.get(`${this.baseUrl}/api/products/:id`)
  }

  getAllProducts(){
    return this.http.get(`${this.baseUrl}/api/products`, { headers: this.headers });
  }






}
