import { Component, EventEmitter , Output, Input } from '@angular/core';
import { AppServiceService } from 'src/app/service/app-service.service';

@Component({
  selector: 'app-delete-product',
  templateUrl: './delete-product.component.html',
  styleUrls: ['./delete-product.component.css']
})
export class DeleteProductComponent {
  loading: boolean = false;

  @Output() closeDeleteModal = new EventEmitter();
  @Input() productId!: string;
  @Input() productName!: string;

  constructor(
    private api: AppServiceService
  ){}

  handleCancel(){
    event?.preventDefault();
    this.closeDeleteModal;
  }

  handleDelete(){
    event?.preventDefault();
    const token = localStorage.getItem('select_user');
    this.loading = true;
    if(token){
      this.api.deleteProduct(this.productId).subscribe({ 
        next: (res) => {
        this.loading = false;
        console.log(res);
        this.closeDeleteModal.emit();
      },
      error: () => {
        this.loading = false;
        console.log('could not delete ' + this.productName)
      }
    })
    }
  }

}

<div class="overlay">
    <div class="custom-modal modal-height" tabindex="-1">
        <div class="modal-dialog">
            <div class="modal-content">
                <div class="modal-body">
                    <h1 class="text-center">Do you wish to delete {{ productName }}?</h1>
                    <div class="action-btns">
                        <input 
                        type="button"
                        class="btn btn-default btn-outline-secondary" 
                        (click)="handleCancel()"
                        value='Cancel' />

                        <input 
                        type="button"
                        class="btn btn-danger" 
                        (click)="handleDelete()"
                        [value]="loading ? 'Deleting...' : 'Delete'" /> 
                    </div>  
                </div>
            </div>
        </div>
    </div>
</div>
