import { Component } from '@angular/core';
import { FormBuilder , Validators } from '@angular/forms';
import { AppServiceService } from 'src/app/service/app-service.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-user-login',
  templateUrl: './user-login.component.html',
  styleUrls: ['./user-login.component.css']
})
export class UserLoginComponent {
  constructor( 
    private fb: FormBuilder,
    private api: AppServiceService,
    private router: Router
    ){}

  loading: boolean = false;

  loginForm = this.fb.group({
    email: ['', [Validators.required]],
    password: ['', [Validators.required]],
  });

  onSubmit(){
    this.loading = true;
    if(this.loginForm.value.email && this.loginForm.value.password){
      this.api.userlogin(this.loginForm.value.email, this.loginForm.value.password)
      .subscribe({
        next: (res: any) => {
          this.loading = false;
          // localStorage.setItem('user_data', JSON.stringify(res));
          localStorage.setItem('user_token', res.token);
          this.router.navigate(['/all-products']);
        },
        error:() => {
          this.loading = false
          console.log('couldn\'t login')
        }
      })
    }
  }

  get email(){
    return this.loginForm.get('email');
  }

  get password(){
    return this.loginForm.get('password');
  }
}
<div class="overlay">
    <div class="custom-modal modal-height" tabindex="-1">
        <div class="modal-dialog">
            <div class="modal-content">
                <div class="modal-header">
                    <h1 class="text-center">Login</h1>
                </div>
                <div class="modal-body">
                   <form 
                    [formGroup]="loginForm" 
                    class="form-body" 
                    (ngSubmit)="onSubmit()">
                        <div>
                            <label for="email">Email</label><br/>
                            <input 
                                type="email" 
                                id="email" 
                                class="form-control"
                                formControlName="email"/>
                            <p *ngIf="email?.errors?.['required']" 
                                class="text-danger">Email field cannot be empty</p>
                        </div>
                        <div>
                            <label for="password">Password</label><br/>
                            <input 
                                type="password" 
                                id="password" 
                                class="form-control"
                                formControlName="password"/>
                            <p *ngIf="password?.errors?.['required']" 
                                class="text-danger">Password field cannot be empty</p>
                        </div>
                        <input 
                            type="submit" 
                            class="btn btn-primary w-100 mt-3" 
                            [value]="loading ? 'loading...' : 'Login'" />
                   </form>
                </div>
                <p class="bottom-text">
                    Don't have an account? &nbsp;<a href="signup">Sign Up</a>
                </p>
            </div>
        </div>
    </div>
</div>
