import { Component, Output, EventEmitter, OnInit } from '@angular/core';
import { AppServiceService } from 'src/app/service/app-service.service';

type Products = {
  name: string;
  description: string;
}

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css'],
})
export class ProductsComponent implements OnInit {

  constructor( private api: AppServiceService ){}

  products!: any;
  loading = false;
  displayAddProductModal: boolean = false; //display button when user records exists in db
  displayEditProductModal: boolean = false;
  displayDeleteProductModal: boolean = false;

  // products = [
  //   { name: "Fish hook", description: "For baiting and catching fish"},
  //   { name: "Anonymitter", description: "The Anonymous identifier" }, 
  //   { name: "Fish hook", description: "For baiting and catching fish"},
  //   { name: "Anonymitter", description: "The Anonymous identifier" }, 
  //   { name: "Fish hook", description: "For baiting and catching fish"},
  //   { name: "Anonymitter", description: "The Anonymous identifier" },
  //   { name: "Fish hook", description: "For baiting and catching fish"},
  //   { name: "Anonymitter", description: "The Anonymous identifier" }, 
  //   { name: "Fish hook", description: "For baiting and catching fish"},
  //   { name: "Anonymitter", description: "The Anonymous identifier" },
  //   { name: "Fish hook", description: "For baiting and catching fish"},
  //   { name: "Anonymitter", description: "The Anonymous identifier" }
  // ]



  // toggle 'add product' modal
  addProductModal(value: boolean){ 
    this.displayAddProductModal = value;
  }

 // toggle 'edit product' modal 
  editProductModal(value: boolean){
    this.displayEditProductModal = value;
  }

  // toggle 'delete product' modal
  deleteProductModal(value: boolean){
    this.displayDeleteProductModal = value;
  }

  ngOnInit(): void {
    this.loading = true;
    this.api.getAllProducts().subscribe((data) => { 
      this.loading = false
      this.products = data
    })
  }

}
