import { Component, EventEmitter, Output } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent {
  @Output() addProductModal = new EventEmitter();

  newProductForm = new FormGroup({
    name: new FormControl(''),
    description: new FormControl(''),
  });

  errors = {
    name: '',
    description: ''
  }

  newProductSubmit(name: string, description: string){
    if(!name){
      this.errors.name = "The name field is empty"
    }
    if(!description){
      this.errors.name = "The description field is empty"
    }
    if(name && description && !this.errors.name && !this.errors.description){
      console.log({ name, description })
    }
  }

}
<div class="overlay">
    <div class="custom-modal modal-height" tabindex="-1">
        <div class="modal-dialog">
            <div class="modal-content">
                <div class="modal-header">
                    <h1>New Product</h1>
                    <i
                    class="bi bi-x-circle-fill text-primary" 
                    (click)="addProductModal.emit()"></i> 
                </div>
                <div class="modal-body">
                   <form formGroup="newProductForm" class="form-body" (submit)="newProductSubmit(name, description)">
                        <div>
                            <label for="name">Name</label><br/>
                            <input 
                                type="text" 
                                id="name" 
                                class="form-control"
                                formControlName="name"/>
                        </div>
                        <p ></p>
                        <div>
                            <label for="desc">Description</label><br/>
                            <textarea 
                                type="text" 
                                id="desc"
                                class="form-control"
                                rows="7"
                                formControlName="description"></textarea>
                        </div><br/><br/>
                        <input 
                            type="submit" 
                            class="btn btn-primary w-100" 
                            value="Add Product" />
                   </form>
                </div>
                <div class="modal-footer"></div>
            </div>
        </div>
    </div>
</div>
