import { Component, EventEmitter, Output } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent {
  @Output() addProductModal = new EventEmitter();

  newProductForm = new FormGroup({
    name: new FormControl(''),
    description: new FormControl(''),
  });

  errors = {
    name: '',
    description: ''
  }

  newProductSubmit(name: string, description: string){
    if(!name){
      this.errors.name = "The name field is empty"
    }
    if(!description){
      this.errors.name = "The description field is empty"
    }
    if(name && description && !this.errors.name && !this.errors.description){
      console.log({ name, description })
    }
  }

}
