import { Component, EventEmitter, Output, Input } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { HotToastService } from '@ngneat/hot-toast';
import { AppServiceService } from 'src/app/service/app-service.service';

@Component({
  selector: 'app-edit-product',
  templateUrl: './edit-product.component.html',
  styleUrls: ['./edit-product.component.css']
})
export class EditProductComponent {

  @Output() closeEditModal = new EventEmitter();
  @Input() id!: string;
  @Input() pr_name!: string;
  @Input() price!: number;
  @Input() pr_description!: string;

  loading: boolean = false;


  constructor( 
    private fb: FormBuilder,
    private api: AppServiceService,
    private toast: HotToastService
   ){}

  editProductForm = this.fb.group({
    name: ['', [Validators.required]],
    price: [0],
    description: ['', [Validators.required]],
  });

  submit(){
    event?.preventDefault();
    this.loading = true;
    this.api.editProduct(this.id, this.pr_name, this.price, this.pr_description)
    .subscribe({
      next: () => {
        this.loading = false;
        this.toast.success('Update Successful');
        this.closeEditModal.emit();
      },
      error: (err) => {
        this.loading = false;
        this.toast.error(err.error.message);
      }
    })
  }

  get name(){
    return this.editProductForm.get('name');
  }

  get description(){
    return this.editProductForm.get('description');
  }

}

<div class="overlay">
    <div class="custom-modal modal-height" tabindex="-1">
        <div class="modal-dialog">
            <div class="modal-content">
                <div class="modal-header">
                    <h1>Edit Product</h1>
                    <i
                    class="bi bi-x-circle-fill text-primary" 
                    (click)="closeEditModal.emit()"></i> 
                </div>
                <div class="modal-body">
                    <form [formGroup]="editProductForm" class="form-body" (ngSubmit)="submit()">
                        <div>
                            <label for="name">Name</label><br/>
                            <input 
                                type="text" 
                                id="name" 
                                class="form-control"
                                [placeholder]="pr_name"
                                formControlName="name"/>
                            <p *ngIf="name?.errors?.['required']" 
                                class="text-danger">Name field must not be empty</p>
                        </div>
                        <div>
                            <label for="price">Price</label><br/>
                            <input 
                                type="number" 
                                id="price" 
                                [placeholder]="price"
                                class="form-control"
                                formControlPrice="price"/>
                        </div>
                        <div>
                            <label for="desc">Description</label><br/>
                            <textarea 
                                type="text" 
                                id="desc"
                                [placeholder]="pr_description"
                                class="form-control"
                                rows="4"
                                formControlName="description"></textarea>
                            <p *ngIf="description?.errors?.['required']" 
                                class="text-danger">Description field must not be empty</p>
                        </div><br/><br/>
                        <input 
                            type="submit" 
                            class="btn btn-primary w-100" 
                            [value]="loading ? 'Updating product...' : 'Submit'" />
                   </form>
                </div>
                <div class="modal-footer"></div>
            </div>
        </div>
    </div>
</div>
