import { Component, Output, EventEmitter } from '@angular/core';

type Products = {
  name: string;
  description: string;
}

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css'],
})
export class ProductsComponent {
  // products!: Products[] | [];
  displayAddProductModal: boolean = false; //display button when user records exists in db
  displayEditProductModal: boolean = false;

  products = [
    { name: "Fish hook", description: "For baiting and catching fish"},
    { name: "Anonymitter", description: "The Anonymous identifier" }
  ]



  // toggle 'add product' modal
  addNewProduct(value: boolean){ 
    this.displayAddProductModal = value;
  }


 // close 'edit product' modal from child component 
  onEditProductModal(){
    this.displayEditProductModal = false;
  }

}
